    <script>
    let fullDate = new Date();  //取得今天日期
    let Year = fullDate.getFullYear();
    let Month = (fullDate.getMonth() + 1) >= 10 ? (fullDate.getMonth() + 1) : (""0"" + (fullDate.getMonth() + 1));
    let date = fullDate.getDate();
